import { Component } from '@angular/core';
import { UsersService } from '../users.service';
import { Router } from '@angular/router';


@Component({
  selector: 'app-country-block',
  templateUrl: './country-block.component.html',
  styleUrls: ['./country-block.component.css']
})
export class CountryBlockComponent {
  constructor(private user: UsersService, private router: Router){}
message:any = ' '
myCountry:any = {}
officialName:any = ''

title = 'Where in the World'

  ngOnInit() {
this.user.currentMessage.subscribe(message => this.message = message)
console.log(this.message)
if (this.message == 'default'){
  this.router.navigateByUrl('countrySearch')
}else {
  this.user.getRestData(this.message).subscribe(data=>
  this.myCountry = Object.entries(data)[0][1],
  this.officialName = this.myCountry.name
  )
  }

}



}
<html>
    <head>

        <meta charset="UTF-8">
        <meta name="viewport" content="width=device-width, initial-scale=1.0">
        <link rel="preconnect" href="https://fonts.googleapis.com">
        <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin>
        <link href="https://fonts.googleapis.com/css2?family=Bacasime+Antique&display=swap" rel="stylesheet">
      </head>
      <body>

      <header class="title">
        <h2>{{title}}</h2> 
      </header>




      <img src='{{ myCountry.flags.png }}'>
      <h2>{{ myCountry.name.official }}</h2>
      <p>Population: {{ myCountry.population }}</p>
      <p>Region: {{ myCountry.region }}</p>
      <p>Capital: {{ myCountry.capital }}</p>
      <p>Subregion: {{ myCountry.subregion}}</p>
      <p>Currency: {{ myCountry.currencies.values }}</p>
      <p>Languages: {{ myCountry.languages }}</p>

      </body>

</html>
