import { Component, OnInit, Output, EventEmitter, Input } from '@angular/core';
import { UsersService } from '../users.service'

@Component({
  selector: 'app-map-search',
  templateUrl: './map-search.component.html',
  styleUrls: ['./map-search.component.css']
})
export class MapSearchComponent  {
title = 'Where in the world?'

officialName = ''
population = ''
region = ''
capital = ''
flag = ''

constructor(private user: UsersService){}
ngOnInit() {
  this.user.getMoreData().subscribe(data => {
    let dataSet = Object.entries(data)[0][1]
      this.officialName = dataSet.name.official
      this.flag = dataSet.flags.png
      this.population = dataSet.population
      this.region = dataSet.region
      this.capital = dataSet.capital
      console.log(dataSet.region)
  })
}

}
