import { Component, OnInit, Output, EventEmitter, Input } from '@angular/core';
import { UsersService } from '../users.service'

@Component({
  selector: 'app-map-search',
  templateUrl: './map-search.component.html',
  styleUrls: ['./map-search.component.css']
})
export class MapSearchComponent  {
title = 'Where in the world?'


populations: any = []
regions: any = []
capitals: any = []
flags: any = []
countries: any= []
countryName = ''
id = ''
myArray = {}
dataSet = ''
constructor(private user: UsersService){}

myObject: any = {}
ngOnInit() {
  this.user.getAllData().subscribe(allData => {
    let myloop = Object.entries(allData);

    for(let i = 0; i < myloop.length; i++){  
    let countryName: any = Object.entries(allData)[i][1].name.common
    this.countries.push(countryName)


  this.user.getMoreData(countryName).subscribe(data => {

    let dataSet: any = Object.entries(data)[0][1]
    this.flags.push(dataSet.flags.png)
    this.populations.push(dataSet.population)
    this.regions.push(dataSet.region)
    this.capitals.push(dataSet.capital)
  })

    }

} 

)}


}
