import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http'
import { MapsvgComponent } from './mapsvg/mapsvg.component'

@Injectable({
  providedIn: 'root'
})
export class UsersService {

  constructor(private http: HttpClient) { }

  getData(id: string)
  {
    let url = "https://api.worldbank.org/v2/country" + '/' + id + '?format=json';

    return this.http.get(url)
  }

  getMoreData(id: string)
  {
    console.log(id)
  let restUrl: any = 'https://restcountries.com/v3.1/name/' + id + '?fullText=true';
    return this.http.get(restUrl)
  }

  getAllData(){
    let allUrl: any = 'https://restcountries.com/v3.1/all?fields=name';
    return this.http.get(allUrl)
  }
}
