import { Component } from '@angular/core';

@Component({
  selector: 'app-mapsvg',
  templateUrl: './mapsvg.component.svg',
  styleUrls: ['./mapsvg.component.css'],
  @ViewChild('svgObject') svgObject: ElementRef;
})
export class MapsvgComponent {

}
window.addEventListener("load", function() {
  svgObject = document.getElementById('svg-object').getSVGDocument().getElementById("map");

 svgObject.querySelectorAll("path").forEach(element => {
  element.addEventListener("mouseover", function(){
    element.style.fill = "#e4b1ab"
  });

  element.addEventListener("mouseover", function(){
    element.style.fill = ""  
  });

  element.addEventListener("click", function(){
    async function getApi(){
    let url = "https://api.worldbank.org/v2/country";
    let country = url + '/' + element.id + '?format=json';
    let response = await fetch(country);
    let data = await response.json();
    let dataSet = data[1][0];
    let name = dataSet.name
    document.getElementById('name').innerHTML = name
    let capital = dataSet.capitalCity
    document.getElementById('capital').innerHTML = capital
    let region = dataSet.region.value
    document.getElementById('region').innerHTML = region
    let incomeLvl = dataSet.incomeLevel.value
    document.getElementById('incomeLvl').innerHTML = incomeLvl
    let longitude = dataSet.longitude
    document.getElementById('longitude').innerHTML = longitude
    let latitude = dataSet.latitude
    document.getElementById('latitude').innerHTML = latitude
    }

    getApi();
  });
});
});
